import "../Style/dashboard.css"
import $ from 'jquery';
import { useState } from 'react';


const dashOpen = () => {
    $('.dashboard').addClass("open");
    $('.dashOpen').addClass("hidden");
};

const dashClose = () => {
    $('.dashboard').removeClass("open");
    $('.dashOpen').removeClass("hidden");
};

function dashboard() {






    const [currentWound, setWound] = useState(wounds);

    const handleDecreaseWound = () => {
        setWound((prevWound) => prevWound - 1);
        if (currentWound - 1 < (wounds / 2)) {
            console.log('slowed');
            $(`[name='${id}']`).addClass("slowed");
        }
        if (currentWound - 1 === 0) {
            $(`[name='${id}']`).addClass("dead");
        }
    };







    return (
        <div className="dashboard">
            <div className="dashOpen" onClick={dashOpen}><div className="dot"></div><div className="dot"></div><div className="dot"></div></div>
            <div className="dashClose" onClick={dashClose}><div className="dot"></div><div className="dot"></div><div className="dot"></div></div>
            <div className="dashArea">
                <div className="dashLeft dashOutside">
                    <div className="cpTitle scoreTitle">CP</div>
                    <div className="cpContent scoreContent"><div className="addMinus">-</div>5<div className="addMinus">+</div></div>
                    <div className="vpTitle scoreTitle">VP</div>
                    <div className="vpContent scoreContent"><div className="addMinus">-</div>5<div className="addMinus">+</div></div>
                </div>
                <div className="dashMiddle"></div>
                <div className="dashRight dashOutside"></div>

            </div>
        </div>
    );
}

export default dashboard;
