import "../Style/grid.css"
import operativeOne from '../images/TauGuy.jpeg';

const operativesArray = [
    {
        id: 1,
        image: operativeOne,
        operativeName: "Object 1",
        type: "Type 1",
        M: 10,
        APL: 5,
        GA: 3,
        DF: 8,
        SV: 6,
        W: 7,
        Weapons: [
            {
                weaponName: "weapon name",
                A: 1,
                BS: 1,
                D: 1,
            },
            {
                weaponName: "weapon name",
                A: 1,
                BS: 1,
                D: 1,
            }
        ],
        UniqueActions: ["Action 1", "Action 2"],
        KeyWords: ["Keyword 1", "Keyword 2"]
    },
    {
        id: 2,
        image: operativeOne,
        operativeName: "Object 1",
        type: "Type 1",
        M: 10,
        APL: 5,
        GA: 3,
        DF: 8,
        SV: 6,
        W: 7,
        Weapons: [
            {
                weaponName: "weapon name",
                A: 1,
                BS: 1,
                D: 1,
            },
            {
                weaponName: "weapon name",
                A: 1,
                BS: 1,
                D: 1,
            }
        ],
        UniqueActions: ["Action 1", "Action 2"],
        KeyWords: ["Keyword 1", "Keyword 2"]
    },
    // Additional objects can be added here
];

const Card = ({ content, type, image }) => {
    return <div className="operativeCard">


        {content}{type}
        <img className="operativeImage" src={image} alt="" />
        </div>;
};


function Grid() {
    return (
        <div id="operativesGrid">
            {operativesArray.map((card) => (
                <Card key={card.id} content={card.operativeName} type={card.type} image={card.image}/>
            ))}
        </div>
    );
}

export default Grid;
